/* assured that it should end like:
     9F                                # array(*)
         6E                             # text(14)
            5265662D42494E4152592D693136 # "Ref-BINARY-i16"
         FF                             # primitive(*)
*/

var buf = msg.payload

buf.writeUInt8(0x9f, buf.length - 17);
buf.writeUInt8(0x6e, buf.length - 16);
buf.writeUInt8(0xff, buf.length - 1);

return msg;

// see https://nodejs.org/api/buffer.htm

let out = new Array(msg.payload.length/2);
let i=0;
for(i=0; i<msg.payload.length/2; i++) {
    out[i] = msg.payload.readInt16LE(2*i);
}

msg.payload =out;

return msg;

// see https://nodejs.org/api/buffer.htm
// output : 0,1,2 buffer messages of size 8820 bytes - where each message has as offset 4410 bytes
// state  : buffer context variable
//

let window = flow.get("window");

// initialize with values for testing
if (typeof window == 'undefined'){
    node.warn("defining window");
    window = {
        "size"    : 10, // size of the window
        "shift"   : 4,  // next window will be shifted with 4 bytes compared to current window
        "buffer"  : Buffer.alloc(10),
        "length"  : 0,   // number of bytes currently assigned in buffer.
        "offset_since_start" : 0
    }
    flow.set("window",window);
}

// indicates up to which part of the msg.payload buffer is already processed.
let payloadOffset = 0;  

// array of output messages
let outputMsgs = [];

// prepare array of output messages to send
while ( (window.length + (msg.payload.length-payloadOffset)) >= window.size){
    outputBuffer=Buffer.alloc(window.size);

    window.buffer.copy(outputBuffer,0,0,window.length);
    msg.payload.copy(outputBuffer,
                     window.length,
                     payloadOffset,
                     payloadOffset+(window.size-window.length));
    payloadOffset = payloadOffset + (window.size-window.length);

    outputMsgs.push({ "payload" : outputBuffer,
                      "offset"  : window.offset_since_start
    });

    // set window buffer to output buffer shifted with window.shift
    outputBuffer.copy(window.buffer,0,window.shift,window.size);
    window.length = window.size-window.shift;
    window.offset_since_start = window.offset_since_start + window.shift;
}


// copy remaining part of msg.payload to window buffer
msg.payload.copy(window.buffer,
                 window.length,
                 payloadOffset,
                 msg.payload.length)
window.length = window.length + msg.payload.length - payloadOffset;

// store the window
flow.set("window",window);

return [outputMsgs];

